<?phprequire_once("inc/session.php");require_once("inc/user.php");$current = mysql_real_escape_string($_POST["current"]);$new = mysql_real_escape_string($_POST["new"]);$confirm = mysql_real_escape_string($_POST["confirm"]);$username = $_SESSION["username"];if (empty($new)){	exit("Password cannot be empty");}if ($new != $confirm){	exit("Passwords do not match");}$expected_hash = mysql_fetch_array(mysql_query("SELECT hash FROM users WHERE username = '$username';"))["hash"];if (!password_verify($current, $expected_hash)){	exit("Bad current password");}$hash = password_hash($new, PASSWORD_DEFAULT);mysql_query("UPDATE users SET hash = '$hash' WHERE username = '$username';");exit("Password changed");?>